import {PayloadAction, createSlice} from '@reduxjs/toolkit'
import { RootState } from '../store'
import { act } from 'react-dom/test-utils'

interface UserType {
    avatarUrl: string,
    createdAt: string,
    email: string,
    fullName: string,
    passwordHash: string,
    updatedAt: string,
    _id: string,
  }

type ItemsType = {
    count: any
    createdAt: string,
    tags: string[],
    text: string,
    title: string,
    updatedAt: string,
    _id: string,
    user: UserType,
    viewsCount: number
}

interface InitialType {
    commentsNew: boolean,
    commentsQuantity: ItemsType[]
}


const initialState: InitialType = {
    commentsNew: false,
    commentsQuantity: []
}

const commentsSlice = createSlice({
    name: 'comments',
    initialState,
    reducers: {
        setNewComments(state, action: PayloadAction<boolean>) {
            state.commentsNew = action.payload
