import {createApi, fetchBaseQuery} from '@reduxjs/toolkit/query/react'

const BASE_URL = 'http://localhost:4444'

export const postsApi = createApi({
  reducerPath: 'posts',
  baseQuery: fetchBaseQuery(
    {baseUrl: BASE_URL}
  ),
  endpoints: (builder) => ({
    getPost: builder.query({
      query: (sort) => {
        console.log(sort)
        if (sort === 0) {
          return {
            url: `/posts?sort=${sort}`,
            method: 'GET'
          }
        } 
        if (sort === 1) {
          return {
            url: `/posts?sort=${sort}`,
            method: 'GET'
          }
        }
        if (sort === 0) {
          return {
            url: `/posts?sort=${sort}`,
            method: 'GET'
          }
        }
      }
    }),

    getTags: builder.query({
        query: () => {
            return {
                url: '/tags',
                method: 'GET'
            }
        }
    }),

    getOnePost: builder.query({
      query: ({id}) => {
        return {
          url: `/posts/${id}`,
          method: 'GET'
        }
      }
    }),

    onePost: builder.mutation({
      query: ({id}) => {
        return {
          url: `/posts/${id}`,
          method: 'GET'
        }
      }
    }),

    removePost: builder.mutation({
      query: ({id}) => {
        console.log(id)
        return {
          url: `/posts/${id}`,
          method: 'DELETE',
          headers: {
            Authorization: window.localStorage.getItem('token')
          }
        }
      }
    }),

    createPost: builder.mutation({
      query: ({title, tags, value, imageUrl}) => {
        return {
          url: '/posts',
          method: 'POST',
          body: {
            title: title,
            tags: tags.replaceAll(' ', '').split(','),
            text: value,
            imageUrl: imageUrl
          },
          headers: {
            Authorization: window.localStorage.getItem('token')
          }
        }
      }
    }),

    updatePost: builder.mutation({
      query: ({id, title, tags, value, imageUrl}) => {
        return {
          url: `/posts/${id}`,
          method: 'PATCH',
          body: {
            title: title,
            tags: tags.replaceAll(' ', '').split(','),
            text: value,
            imageUrl: imageUrl
          },
          headers: {
            Authorization: window.localStorage.getItem('token')
          }
        }
      }
    })
  }),
})


export const { 
  useGetPostQuery, 
  useGetTagsQuery, 
  useGetOnePostQuery, 
  useOnePostMutation,
  useRemovePostMutation, 
  useCreatePostMutation,
  useUpdatePostMutation
} = postsApi
