import {PayloadAction, createSlice} from '@reduxjs/toolkit'

const initialState = {
    posts: []
}

const posts = createSlice({
    name: 'posts',
    initialState,
    reducers: {
        setPost(state, action) {
            state.post = action.payload
        }
    }
})

export const {setPost} = posts.actions

export default posts.reducer
