import {PayloadAction, createSlice} from '@reduxjs/toolkit'

const initialState = {
    posts: {
        items: [],
        status: 'loading'
    }
}

const posts = createSlice({
    name: 'posts',
    initialState,
    reducers: {
        setPosts(state, action) {
            state.posts = action.payload
        }
    }
})

export const {setPosts} = posts.actions

export default posts.reducer
