import React from "react";
import {useParams} from "react-router-dom";
import ReactMarkdown  from "react-markdown";

import { Post } from "../components/Post";
import { Index } from "../components/AddComment";
import { CommentsBlock } from "../components/CommentsBlock";
import { useGetOnePostQuery } from "../api/postsApi";
import { useGetOneCommentsMutation } from "../api/commentsApi";

import {useSelector, useDispatch} from 'react-redux'
import { setCommentsQuantity } from "../redux/slices/comments";

export const FullPost = () => {

  const {id} = useParams()
  const dispatch = useDispatch()

  const {data, isLoading} = useGetOnePostQuery({id})
  const [comments, {isLoading: loading}] = useGetOneCommentsMutation()
  console.log(data)

  React.useEffect(() => {
    dispatch(setCommentsQuantity(comments?.length))
  }, [id, comments])

  if (isLoading) {
    return <Post isLoading={true} isFullPost/>
  }

  return (
    <>
      <Post
        id={id}
        title={data?.title}
        imageUrl={data.imageUrl ? `http://localhost:4444${data.imageUrl}` : ''}
        user={data?.user}
        createdAt={data?.createdAt}
        viewsCount={data?.viewsCount}
        commentsCount={comments.length}
        tags={data?.tags}
        isFullPost
      >
        <ReactMarkdown children={data.text} />
      </Post>
      <CommentsBlock
        items={comments}
        isLoading={loading}
      >
        <Index id={id ? id : ''}/>
      </CommentsBlock>
    </>
  );
};
