import { configureStore } from '@reduxjs/toolkit'
import postsSlice from './slices/posts'

import { postsApi } from '../api/postsApi'

const store = configureStore({
    reducer: {
        postsSlice,
        [postsApi.reducerPath]: productsApi.reducer,
    }
})

export default store
