import {PayloadAction, createSlice} from '@reduxjs/toolkit'
import { RootState } from '../store'

interface UserType {
    avatarUrl: string,
    createdAt: string,
    email: string,
    fullName: string,
    passwordHash: string,
    updatedAt: string,
    _id: string,
  }

type ItemsType = {
    count: any
    createdAt: string,
    tags: string[],
    text: string,
    title: string,
    updatedAt: string,
    _id: string,
    user: UserType,
    viewsCount: number
}

interface InitialType {
    commentsNew: boolean,
    commentsQuantity: ItemsType[]
}


const initialState: InitialType = {
    commentsNew: false,
    commentsQuantity: []
}

const commentsSlice = createSlice({
    name: 'comments',
    initialState,
    reducers: {
        setNewComments(state, action: PayloadAction<boolean>) {
            state.commentsNew = action.payload
        },

        setCommentsQuantity(state, action: PayloadAction<ItemsType>) {
            // state.commentsQuantity.push(action.payload)

            if (action.payload.count) {
                state.commentsQuantity.push({
                    ...action.payload,
                    count
                })
            } else {
                state.commentsQuantity.push({
                    ...action.payload,
                    count: 1
                })
            }
        }
    }
})

export const commentsType = (state: RootState) => state.commentsSlice

export const { setNewComments, setCommentsQuantity } = commentsSlice.actions

export default commentsSlice.reducer
