import React from "react";
import {useParams} from "react-router-dom";
import ReactMarkdown  from "react-markdown";

import { Post } from "../components/Post";
import { Index } from "../components/AddComment";
import { CommentsBlock } from "../components/CommentsBlock";
import { useGetOnePostQuery } from "../api/postsApi";
import { useGetAllCommentsQuery } from "../api/commentsApi";

export const FullPost = () => {

  const {id} = useParams()

  const {data, isLoading} = useGetOnePostQuery({id})
  const {data: comments} = useGetAllCommentsQuery()
  console.log(data)


  const commentToPost = (comments) => {
    let commentsArray = []
    for (let i = 0; i < comments.length; i++) {
      if (i.paramsId === id) commentsArray.push(i)
      return
    }
    return commentsArray
  }

  console.log(commentToPost(comments))



  if (isLoading) {
    return <Post isLoading={true} isFullPost/>
  }

  return (
    <>
      <Post
        id={id}
        title={data?.title}
        imageUrl={data.imageUrl ? `http://localhost:4444${data.imageUrl}` : ''}
        // imageUrl="https://res.cloudinary.com/practicaldev/image/fetch/s--UnAfrEG8--/c_imagga_scale,f_auto,fl_progressive,h_420,q_auto,w_1000/https://dev-to-uploads.s3.amazonaws.com/uploads/articles/icohm5g0axh9wjmu4oc3.png"
        user={data?.user}
        createdAt={data?.createdAt}
        viewsCount={data?.viewsCount}
        commentsCount={3}
        tags={data?.tags}
        isFullPost
      >
        <ReactMarkdown children={data.text} />
      </Post>
      <CommentsBlock
        items={comments}
        isLoading={false}
      >
        <Index />
      </CommentsBlock>
    </>
  );
};
