import React from "react";
import {useParams} from 'react-router-dom'
import { useFindTagsQuery } from "../../api/tagsApi";
import { Post } from "../../components";


import Grid from "@mui/material/Grid";

export const TagsPost = () => {

    const {id} = useParams()
    console.log(id)
    const {data, isLoading} = useFindTagsQuery({id})
    const userAuth = useSelector((state) => state.authSlice.auth)

    return (
        <div>
            <div style={{
                fontSize: '42px', 
                color: 'rgb(58, 131, 171)', 
                fontWeight: '600', 
                lineHeight: '36px',
                marginBottom: '40px'
            }}>
                #Заметки
            </div>
            <Grid container spacing={4}>
                <Grid xs={8} item>
                    {(isLoading ? [...Array(3)] : data)
                        .map((obj) => {
                            return (
                                <Post
                                    id={obj._id}
                                    title={obj.title}
                                    imageUrl={obj.imageUrl ? `http://localhost:4444${obj.imageUrl}` : ''}
                                    user={obj.user}
                                    createdAt={obj.createdAt}
                                    viewsCount={obj.viewsCount}
                                    commentsCount={3}
                                    tags={obj.tags}
                                    isEditable={userAuth?._id === obj?.user?._id}
                                />
                            )
                        })
                    }
                </Grid>
            </Grid>
        </div>
    )
}
