import React from "react";
import Tabs from "@mui/material/Tabs";
import Tab from "@mui/material/Tab";
import Grid from "@mui/material/Grid";

import { Post } from "../components/Post";
import { TagsBlock } from "../components/TagsBlock";
import { CommentsBlock } from "../components/CommentsBlock";

import {useSelector, useDispatch} from 'react-redux'

import { useGetPostQuery, useGetTagsQuery } from "../api/postsApi";

const tabs = [
  'Все',
  'Новые',
  'Популярные'
]

export const Home = () => {

  // const [data, setData] = React.useState()

  const {data: tags, isLoading: loadingTags} = useGetTagsQuery()

  const userAuth = useSelector((state) => state.authSlice.auth)
  const {removePostsId} = useSelector((state) => state.postsSlice)
  const [sort, setSort] = React.useState(0)

  let sortData
  React.useEffect(() => {
    const sortDataFunc =  () => {
      sortData === sort
    }

    sortDataFunc()
  }, [sort])

  const {data, isLoading: loading, isFetching} = useGetPostQuery(sortData || '')
  console.log(data)
  console.log(tags)


  return (
    <>
      <Tabs
        style={{ marginBottom: 15 }}
        value={sort}
        aria-label="basic tabs example"
      >
        {/* <Tab label="Все" />
        <Tab label="Новые" />
        <Tab label="Популярные" /> */}
        {
          tabs.map((tab, i) => {
            return (
              <Tab key={i} onClick={() => setSort(i)} label={tab} />
            )
          })
        }
      </Tabs>
      <Grid container spacing={4}>
        <Grid xs={8} item>
          {(loading ? [...Array(5)] : data)
            .filter((post) => post?._id !== removePostsId)
            .map((obj, index) => loading ? (<Post key={index} isLoading={true}/>) :
            (
              <Post
                id={obj._id}
                title={obj.title}
                imageUrl={obj.imageUrl ? `http://localhost:4444${obj.imageUrl}` : ''}
                // imageUrl="https://res.cloudinary.com/practicaldev/image/fetch/s--UnAfrEG8--/c_imagga_scale,f_auto,fl_progressive,h_420,q_auto,w_1000/https://dev-to-uploads.s3.amazonaws.com/uploads/articles/icohm5g0axh9wjmu4oc3.png"
                user={obj.user}
                createdAt={obj.createdAt}
                viewsCount={obj.viewsCount}
                commentsCount={3}
                tags={obj.tags}
                isEditable={userAuth?._id === obj?.user?._id}
              />
            ))}
        </Grid>
        <Grid xs={4} item>
          <TagsBlock
            items={tags}
            isLoading={loadingTags}
          />
          <CommentsBlock
            items={[
              {
                user: {
                  fullName: "Вася Пупкин",
                  avatarUrl: "https://mui.com/static/images/avatar/1.jpg",
                },
                text: "Это тестовый комментарий",
              },
              {
                user: {
                  fullName: "Иван Иванов",
                  avatarUrl: "https://mui.com/static/images/avatar/2.jpg",
                },
                text: "When displaying three lines or more, the avatar is not aligned at the top. You should set the prop to align the avatar at the top",
              },
            ]}
            isLoading={false}
          />
        </Grid>
      </Grid>
    </>
  );
};
