import React from "react";

import styles from "./AddComment.module.scss";
import {useSelector, useDispatch} from 'react-redux'

import TextField from "@mui/material/TextField";
import Avatar from "@mui/material/Avatar";
import Button from "@mui/material/Button";
import { setNewComments } from "../../redux/slices/comments";

import { useCreateCommentsMutation } from "../../api/commentsApi";

export const Index = ({id}) => {

  const dispatch = useDispatch()

  const inputRef = React.useRef(null)
  const {auth} = useSelector((state) => state.authSlice)
  const [value, setValue] = React.useState('')
  const [createComments, {isLoading}] = useCreateCommentsMutation()
  console.log(value)

  const onSubmit = () => {
    setNewComments(true)
    createComments({value, id})
    setValue('')
    inputRef.current.blur()
    setTimeout(() => {
      setNewComments(false)
    }, 3000);
  }

  return (
    <>
      <div className={styles.root}>
        <Avatar
          classes={{ root: styles.avatar }}
          src={`${auth.avatarUrl}`}
        />
        <div className={styles.form} style={{position: 'relative'}}>
          <TextField
            label="Написать комментарий"
            variant="outlined"
            maxRows={10}
            value={value}
            onChange={(e) => setValue(e.target.value)}
            multiline
            fullWidth
            ref={inputRef}
          />
          <Button type="submit" onClick={onSubmit} variant="contained">Отправить</Button>
          {
            true && (
              <div style={{
                display: 'flex',
                flexDirection: 'column', 
                backgroundColor: '#15ea9f', 
                padding: '20px 10px', 
                position: 'absolute', 
                right: '0px', 
                bottom: '0px',
                borderRadius: '10px'

              }}>
                <div>Комментарий создан</div>
              </div>
            )
          }
        </div>
      </div>
    </>
  );
};
