import React from "react";

import styles from "./AddComment.module.scss";
import {useSelector, useDispatch} from 'react-redux'

import TextField from "@mui/material/TextField";
import Avatar from "@mui/material/Avatar";
import Button from "@mui/material/Button";

import { useCreateCommentsMutation } from "../../api/commentsApi";

export const Index = ({id}) => {

  const dispatch = useDispatch()

  const inputRef = React.useRef(null)
  const {auth} = useSelector((state) => state.authSlice)
  const [value, setValue] = React.useState('')
  const [createComments, {isLoading}] = useCreateCommentsMutation()
  console.log(value)

  const onSubmit = () => {
    createComments({value, id})
    setValue('')
    (inputRef.current.blur)
  }

  return (
    <>
      <div className={styles.root}>
        <Avatar
          classes={{ root: styles.avatar }}
          src={`${auth.avatarUrl}`}
        />
        <div className={styles.form}>
          <TextField
            label="Написать комментарий"
            variant="outlined"
            maxRows={10}
            value={value}
            onChange={(e) => setValue(e.target.value)}
            multiline
            fullWidth
            ref={inputRef}
          />
          <Button type="submit" onClick={onSubmit} variant="contained">Отправить</Button>
        </div>
      </div>
    </>
  );
};
