import React from "react";
import TextField from "@mui/material/TextField";
import Paper from "@mui/material/Paper";
import Button from "@mui/material/Button";
import SimpleMDE from "react-simplemde-editor";

import {useSelector, useDispatch} from 'react-redux'
import {Navigate} from 'react-router-dom'

import "easymde/dist/easymde.min.css";
import styles from "./AddPost.module.scss";

import { useAddFileMutation } from "../../api/imageApi";

export const AddPost = () => {
  const  [imageUrl, setImageUrl] = React.useState('')
  const [value, setValue] = React.useState("");
  const [title, setTitle] = React.useState("");
  const [tags, setTags] = React.useState("");
  const inputRef = React.useRef(null)

  const {auth} = useSelector((state) => state.authSlice)

  const [addFile] = useAddFileMutation()

  const onChange = React.useCallback((value) => {
    setValue(value);
  }, []);

  const onClickRemoveImage = () => {
    setImageUrl('')
  }

  const handleChangeFile = async (event) => {
    // console.log(event.target.files)
    try {

      const formData = new FormData()
      const file = event.target.files[0]

      formData.append('image', file)

      const {data} = await addFile(formData)

      setImageUrl(data.url)

    } catch (error) {
      console.log(error)
      alert('Ошибка при загрузке файла')
    }
  }

  const onSubmit = async () => {
    try {



    } catch (error) {
      console.log(error)
    }
  }

  const options = React.useMemo(
    () => ({
      spellChecker: false,
      maxHeight: "400px",
      autofocus: true,
      placeholder: "Введите текст...",
      status: false,
      autosave: {
        enabled: true,
        delay: 1000,
      },
    }),
    []
  );

  if (!window.localStorage.getItem('token') && !Object.keys(auth || '').length > 0) {
    return <Navigate to="/" />
  }

  console.log(imageUrl)

  return (
    <Paper style={{ padding: 30 }}>
      <Button variant="outlined" size="large" onClick={() => inputRef.current.click()}>
        Загрузить превью
      </Button>
      <input ref={inputRef} type="file" onChange={handleChangeFile} hidden/>
      {
        imageUrl && (
          <>
            <div style={{paddingLeft: '24px', display: 'inline-flex'}}>
              <Button variant="contained" color="error" onClick={onClickRemoveImage}>
                Удалить
              </Button>
            </div>
            <img className={styles.image} src={`http://localhost:4444/${imageUrl}`} alt="Uploaded" />
          </>
        )
      }
      <br />
      <br />
      <form>
        <TextField
          classes={{ root: styles.title }}
          variant="standard"
          placeholder="Заголовок статьи..."
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          fullWidth
        />
        <TextField
          classes={{ root: styles.tags }}
          variant="standard"
          placeholder="Тэги"
          value={tags}
          onChange={(e) => setTags(e.target.value)}
          fullWidth
        />
        <SimpleMDE
          className={styles.editor}
          value={value}
          onChange={onChange}
          options={options}
        />
        <div className={styles.buttons}>
          <Button onClick={onSubmit} size="large" variant="contained">
            Опубликовать
          </Button>
          <Button size="large">Отмена</Button>
      </div>
      </form>
    </Paper>
  );
};
