import React from "react";
import {useParams} from 'react-router-dom'
import {useSelector, useDispatch} from 'react-redux'

import { useFindTagsQuery } from "../../api/tagsApi";
import { Post } from "../../components";
import { CommentsBlock } from "../../components";

import Grid from "@mui/material/Grid";

export const TagsPost = () => {

    const {id} = useParams()
    console.log(id)
    const {data, isLoading: loading} = useFindTagsQuery({id})
    const userAuth = useSelector((state) => state.authSlice.auth)

    const {data: comments, isLoading: Commloading} = useGetOneCommentsQuery({id})

    return (
        <div>
            <div style={{
                fontSize: '42px', 
                color: 'rgb(58, 131, 171)', 
                fontWeight: '600', 
                lineHeight: '36px',
                marginBottom: '40px'
            }}>
                #Заметки
            </div>
            <Grid container spacing={4}>
                <Grid xs={8} item>
                    {(loading ? [...Array(3)] : data)
                        .map((obj, index) => loading ? (<Post key={index} isLoading={true}/>) : (
                            <Post
                                key={index}
                                id={obj._id}
                                title={obj.title}
                                imageUrl={obj.imageUrl ? `http://localhost:4444${obj.imageUrl}` : ''}
                                user={obj.user}
                                createdAt={obj.createdAt}
                                viewsCount={obj.viewsCount}
                                commentsCount={3}
                                tags={obj.tags}
                                isEditable={userAuth?._id === obj?.user?._id}
                            />
                        ))
                    }
                </Grid>
                <Grid xs={4} item>
                    <CommentsBlock
                        items={[
                        {
                            user: {
                            fullName: "Вася Пупкин",
                            avatarUrl: "https://mui.com/static/images/avatar/1.jpg",
                            },
                            text: "Это тестовый комментарий",
                        },
                        {
                            user: {
                            fullName: "Иван Иванов",
                            avatarUrl: "https://mui.com/static/images/avatar/2.jpg",
                            },
                            text: "When displaying three lines or more, the avatar is not aligned at the top. You should set the prop to align the avatar at the top",
                        },
                        ]}
                        isLoading={false}
                    />
                </Grid>
            </Grid>
        </div>
    )
}
