import {PayloadAction, createSlice} from '@reduxjs/toolkit'
import { RootState } from '../store'

const initialState = {
    commentsNew: false,
    commentsQuantity: 0
}

const commentsSlice = createSlice({
    name: 'comments',
    initialState,
    reducers: {
        setNewComments(state, action: PayloadAction<boolean>) {
            state.commentsNew = action.payload
        },

        setCommentsQuantity(state, action: PayloadAction<number>) {

        }
    }
})

export const commentsType = (state: RootState) => state.commentsSlice

export const { setNewComments } = commentsSlice.actions

export default commentsSlice.reducer
