import {createSlice} from '@reduxjs/toolkit'

const initialState = {
    setRemovePostsId: []
}

const postsSlice = createSlice({
    name: 'posts',
    initialState,
    reducers: {
        setRemovePostsId(state, action) {
            state.removePosts = action.payload
        }
    }
})

export const {setRemovePosts} = postsSlice.actions

export default postsSlice.reducer
